import { Component } from "react";
import Sidebar from "./sidebar";

import 'bootstrap/dist/css/bootstrap.min.css';
import "react-bootstrap-table-next/dist/react-bootstrap-table2.min.css";

import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import filterFactory, {textFilter, numberFilter } from 'react-bootstrap-table2-filter';
import { Button,Card,CardTitle,Col,Label,Modal,ModalBody,ModalHeader,Row,Form,FormGroup,Input } from 'reactstrap';
import { voterList, getVoter, editVoter, deleteVoter, registerVoter } from "../../../api";
import Loading from "../../Loading";

const validateEmail = (email) => {
    return String(email)
      .toLowerCase()
      .match(
        /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
      );
};


class Voters extends Component{

    constructor(props){
        super(props);

        this.state = {
            edit : false,
            add : false,
            voterList : '',
            fullName : '',
            email : '',
            id : '',
            adminUniversity: '',
            password : '',
            confirmPassword : '',
            error : ''
        }
    }




    toggleEdit = (event) => {
        event.preventDefault();
        this.setState({
            edit: !this.state.edit
        })
    }

    toggleAdd = (event) => {
        event.preventDefault();
        this.setState({
            add: !this.state.add
        })
    }


    onChangeInput = (event) => {
        let name = event.target.name;
        let value = event.target.value;

        this.setState({
            [name] : value
        })
    }

    handleEdit = (event) => {
        event.preventDefault();
        // id :- event.target.value
        // console.log(event.target.value)
        // alert("edit", event.target.value)

        this.setState({
            error : ''
        })



        let voterId = event.target.value;
        console.log("Voter id ", voterId)
        let body = {
            'voterId' : voterId
        }

        this.setState({
            id : voterId
        },  () => {setTimeout(()=>{},100)})

        getVoter(body).then(res => {
            console.log(res)
            let data = res['data']
            if(data['message'] == 'success'){
                console.log("Yes")
                let body = data['data']
                let fullName = body['fullName']
                let email = body['email']

                this.setState({
                    fullName : fullName,
                    email : email
                }, () => {setTimeout(()=>{this.setState({edit : true})},1)})



            }

        })

        console.log(this.state.fullName)




    }

    handleAdd = (event) => {
        event.preventDefault();
        //alert("add", event.target.value)

        this.setState({
            add : true,
            fullName : '',
            email : '',
            password : '',
            confirmPassword : '',
            error : '',
            loading: true
        })
    }

    handleDelete = (event) =>{
        event.preventDefault();
        //console.log(event.target.value)
        // alert("edit", event.target.value)

        let voterId = event.target.value;
        let body = {
            'voterId' : voterId,
            'adminUniversity' : this.state.adminUniversity
        }

        deleteVoter(body).then(res => {
            console.log(res)
            let data = res['data']
            if(data['messgae'] == 'success'){
                this.getdata();
                alert("delete")
            }
        })

    }

    handleUpdate = (event) =>{
        event.preventDefault();
        //console.log(this.state.id)
        let body = {
            "voterId" : this.state.id,
            "fullName" : this.state.fullName,
            "email" : this.state.email
        }

        editVoter(body).then(res => {
            console.log(res)
            let data = res['data']
            if(data['message'] == 'success'){
                console.log("here")
                this.getdata();
                this.setState({
                    edit : false
                })
            }
            else{
                this.setState({
                    error : data['error']
                })
            }

        })
    }

    handleCreate = (event) =>{
        event.preventDefault();
        let fullName = this.state.fullName;
        let email = this.state.email;
        let password = this.state.password;
        let confirmPassword = this.state.confirmPassword;
        let university = this.state.adminUniversity;

        this.setState({
            error : ''
        })

        if(password != confirmPassword){
            this.setState({
                error : "password and confirm password must be same"
            })
            return;
        }

        if(!validateEmail(email)){
            this.setState({
                error : "Plesase enter a valid email"
            })

            return;
        }

        let body = {
            "id" : 1,
            "fullName" : fullName,
            "email" : email,
            "password" : password,
            "university" : university
        }


        registerVoter(body).then(res => {
            console.log(res)
            let data = res['data']
            console.log(data)
            if(data['message'] == 'success'){
                console.log("come here in register voter")
                alert("Added")
                this.setState({
                    add : false
                })
                this.getdata()
            }
            else{
                this.setState({
                    error : data['error']
                })
            }
        })

